function extractIdGoogleDrive(url) {
  // Padr√µes de regex para diferentes formatos de URL do Google Drive
  const padroes = [
    /\/open\?id=([a-zA-Z0-9_-]+)/,           // https://drive.google.com/open?id=ID
    /\/file\/d\/([a-zA-Z0-9_-]+)/,           // https://drive.google.com/file/d/ID/view
    /\/uc\?(?:export=download&)?id=([a-zA-Z0-9_-]+)/, // https://drive.google.com/uc?id=ID
    /\/folders\/([a-zA-Z0-9_-]+)/            // https://drive.google.com/drive/folders/ID
  ];

  for (const padrao of padroes) {
    const match = url.match(padrao);
    if (match) {
      return match[1];
    }
  }

  // Retorna null se nenhum padr√£o for encontrado
  return null;
}

